CREATE OR REPLACE FUNCTION obter_se_amostra_liberada (nr_sequencia_p regra_tipo_amostra_pato.nr_sequencia%type, cd_perfil_p regra_tipo_amostra_pato.cd_perfil%type, cd_setor_atendimento_p regra_tipo_amostra_pato.cd_setor_atendimento%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);
ie_existe_w varchar(1);
ie_ativo_w  varchar(1);


BEGIN

ds_retorno_w := 'N';

select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END 
into STRICT ie_ativo_w
from tipo_amostra_patologia
where nr_sequencia = nr_sequencia_p
and coalesce(ie_situacao,'A') ='A';

 if (ie_ativo_w = 'S') then
    ds_retorno_w := 'S';

    select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END 
    into STRICT ie_existe_w
    from regra_tipo_amostra_pato 
    where nr_seq_amostra = nr_sequencia_p;

    if (ie_existe_w = 'S') then
    
     select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END 
     into STRICT ds_retorno_w
     from regra_tipo_amostra_pato
     where nr_seq_amostra = nr_sequencia_p
     and coalesce(cd_perfil, cd_perfil_p) = cd_perfil_p
     and coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p;

    end if;
 end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
