CREATE OR REPLACE FUNCTION obter_se_amostra_ok ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_amostra_ww		integer;
ie_amostra_w		varchar(1);
cd_material_exame_w	varchar(20);


BEGIN

select coalesce(max(ie_amostra),'X'),
	 max(cd_material_exame)
into STRICT	ie_amostra_w,
	cd_material_exame_w
from prescr_procedimento
where nr_prescricao = nr_prescricao_p
  and nr_sequencia  = nr_seq_prescr_p;

if (ie_amostra_w <> 'S') then
	select coalesce(max(1),0)
	into STRICT ie_amostra_ww
	from material_exame_lab
	where cd_material_exame = cd_material_exame_w
	  and ie_volume_tempo = 'N';

	if (ie_amostra_ww = 0) then
		select coalesce(max(1),0)
		into STRICT ie_amostra_ww
		from	material_exame_lab b,
			prescr_proc_material a
		where a.nr_seq_material = b.nr_sequencia
		  and a.nr_prescricao   = nr_prescricao_p
		  and b.cd_material_exame = cd_material_exame_w
		  and a.qt_volume <> 0;
	end if;

	if (ie_amostra_ww = 1) then
		ie_amostra_w := 'S';
	else
		ie_amostra_w := 'N';
	end if;
end if;

return ie_amostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
