CREATE OR REPLACE FUNCTION obter_se_anestesista_escala ( cd_profissional_p bigint, nr_seq_escala_dir_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	escala_diaria
where	cd_pessoa_fisica	=	cd_profissional_p
and	nr_sequencia		=	nr_seq_escala_dir_p;

if (ie_retorno_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	escala_diaria_adic
	where	cd_pessoa_fisica	=	cd_profissional_p
	and	nr_seq_escala_diaria	=	nr_seq_escala_dir_p;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
