CREATE OR REPLACE FUNCTION obter_se_anexo_proj (nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2) := 'S';
dt_inicio_sem_w		timestamp;
dt_fim_sem_w		timestamp;
dt_inicio_real_w	timestamp;
qt_registro_w		bigint;
nr_dia_semana_w		bigint;

BEGIN

select	dt_inicio_prev
into STRICT	dt_inicio_real_w
from	proj_projeto
where	nr_sequencia = nr_seq_projeto_p;

nr_dia_semana_w := obter_cod_dia_semana(dt_inicio_real_w);
dt_inicio_sem_w := dt_inicio_real_w - nr_dia_semana_w;
dt_fim_sem_w	:= fim_dia(dt_inicio_sem_w + 7);

ds_retorno_w := 'S';
while(dt_fim_sem_w < clock_timestamp()) and (ds_retorno_w = 'S') loop
	begin

	select 	count(*)
	into STRICT 	qt_registro_w
	from 	proj_documento
	where 	dt_arquivo between dt_inicio_sem_w and dt_fim_sem_w
	and	nr_seq_tipo_documento = 3;

	if (qt_registro_w = 0) then
		ds_retorno_w := 'N';
	else
		begin
		dt_inicio_sem_w := dt_fim_sem_w + 1;
		dt_fim_sem_w 	:= fim_dia(dt_inicio_sem_w + 7);
		end;
	end if;
	end;
end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
