CREATE OR REPLACE FUNCTION obter_se_aniversario_pac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


/*

NÃO UTILIZAR,
FOI CRIADA PELO PESSOAL DO JAVA
EXISTE A OBTER_SE_ANIVERSARIO_PACIENTE QUE É IGUAL E É UTILIZADA NO DELPHI
*/
ds_retorno_w		varchar(255)	:= '';
ds_paciente_w		varchar(255);
dt_atual_w		timestamp;
ds_ano_w		varchar(255);
dt_nascimento_w		varchar(5);
dt_aniversario_w	varchar(255);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	ds_paciente_w	:= wheb_mensagem_pck.get_texto(309778); --'Paciente';
	select	trunc(clock_timestamp()),
		to_char(clock_timestamp(),'yyyy'),
		substr(obter_dados_pf(cd_pessoa_fisica_p, 'DN'),1,5)
	into STRICT	dt_atual_w,
		ds_ano_w,
		dt_nascimento_w
	;

	if (dt_nascimento_w IS NOT NULL AND dt_nascimento_w::text <> '') then
		if (dt_nascimento_w = '29/02') and (substr(obter_se_ano_bisexto(clock_timestamp()),1,1) = 'N') then
			dt_nascimento_w := '01/03';
		end if;

		dt_aniversario_w := dt_nascimento_w || '/' || ds_ano_w;

		if (to_date(dt_aniversario_w,'dd/mm/yyyy') = dt_atual_w) then
			ds_retorno_w	:= ds_paciente_w || ' ' || wheb_mensagem_pck.get_texto(309779); --' está de aniversário hoje';
		end if;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
