CREATE OR REPLACE FUNCTION obter_se_aniversario_pessoa (cd_pessoa_fisica_p text, qt_dias_intervalo_p bigint) RETURNS varchar AS $body$
DECLARE



dt_nascimento_w		timestamp;
ie_aniversario_w	varchar(001) := 'N';
dt_aniversario_w	timestamp;
ds_ano_w		varchar(10);


BEGIN

select	dt_nascimento
into STRICT	dt_nascimento_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;


if (dt_nascimento_w IS NOT NULL AND dt_nascimento_w::text <> '')	and (trunc(dt_nascimento_w,'dd') <> trunc(clock_timestamp(),'dd'))	then
	begin
	ds_ano_w	:= to_char(clock_timestamp(),'yyyy');
	dt_aniversario_w	:= to_date((to_char(trunc(dt_nascimento_w),'dd/mm')
					|| '/' || ds_ano_W),'dd/mm/yyyy');

	if (dt_aniversario_w	>= trunc(clock_timestamp() - qt_dias_intervalo_p)) and (dt_aniversario_w 	<= trunc(clock_timestamp() + qt_dias_intervalo_p)) then
		ie_aniversario_w	:= 'S';
	end if;

	exception
	when others then
		ie_aniversario_w	:= 'N';
	end;

end if;

return	ie_aniversario_w;

end;
$body$
LANGUAGE PLPGSQL
