CREATE OR REPLACE FUNCTION obter_se_ano_bisexto ( dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ano_w smallint;
ds_retorno_w varchar(1);

BEGIN

select 	(to_char(dt_referencia_p,'yyyy'))::numeric
into STRICT	ano_w
;

if (ano_w mod 4 = 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
