CREATE OR REPLACE FUNCTION obter_se_antimicrobiano (cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


ie_controle_medico_w	smallint := 0;

BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin

	select	max(obter_controle_medic(cd_material,wheb_usuario_pck.get_cd_estabelecimento,ie_controle_medico,null,null,null))
	into STRICT		ie_controle_medico_w
	from		material
	where	cd_material = cd_material_p;

	if (ie_controle_medico_w	= 0) then
		select	count(a.cd_material)
		into STRICT	ie_controle_medico_w
		from	material a,
				medic_ficha_tecnica b
		where	a.cd_material	= cd_material_p
		and		b.nr_sequencia	= a.nr_seq_ficha_tecnica
		and		b.ie_antimicrobiano	= 'S';
	end if;

	end;
end if;

return	ie_controle_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
