CREATE OR REPLACE FUNCTION obter_se_apac_continuidade (nr_seq_apac_p bigint, nr_apac_p text, nr_atendimento_p bigint, ie_tipo_apac_p bigint) RETURNS varchar AS $body$
DECLARE


ie_continuidade_w	varchar(1) := 'N';
qt_continuidade_w	bigint;
nr_seq_primeira_apac_w	bigint;


BEGIN
if (ie_tipo_apac_p = 2) then

	select	count(*)
	into STRICT	qt_continuidade_w
	from	sus_apac_unif
	where	nr_atendimento 	= nr_atendimento_p
	and	nr_apac		= nr_apac_p
	and	ie_tipo_apac 	= ie_tipo_apac_p
	and	nr_sequencia	<> nr_seq_apac_p;

	if (qt_continuidade_w > 0) then

		select	min(a.nr_sequencia)
		into STRICT	nr_seq_primeira_apac_w
		from	sus_apac_unif a
		where	a.nr_atendimento 	= nr_atendimento_p
		and	a.nr_apac		= nr_apac_p
		and	a.ie_tipo_apac 		= ie_tipo_apac_p;

		if (nr_seq_primeira_apac_w <> nr_seq_apac_p) then
			ie_continuidade_w	:= 'S';
		else
			ie_continuidade_w	:= 'N';
		end if;
	else
		ie_continuidade_w	:= 'N';
	end if;

end if;

return	ie_continuidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
