CREATE OR REPLACE FUNCTION obter_se_apac_protocolo ( nr_seq_apac_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_existe_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN 
 
select	count(*) 
into STRICT	qt_existe_w 
from	conta_paciente b, 
	sus_apac_movto a 
where	a.nr_sequencia		= nr_seq_apac_p 
and	a.nr_interno_conta	= b.nr_interno_conta 
and	(b.nr_protocolo IS NOT NULL AND b.nr_protocolo::text <> '') 
and	(b.nr_seq_protocolo IS NOT NULL AND b.nr_seq_protocolo::text <> '');
 
if (qt_existe_w > 0) then 
	ds_retorno_w	:= 'S';
end if;
 
return	ds_retorno_w;
 
END;
