CREATE OR REPLACE FUNCTION obter_se_apae_vincular ( nr_cirurgia_p bigint ) RETURNS varchar AS $body$
DECLARE



qt_apae_cirurgia_w	integer;
cd_pessoa_fisica_w	varchar(10);
ie_possui_apae_w	varchar(1);


BEGIN

select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	cirurgia
where	nr_cirurgia	=	nr_cirurgia_p;

select	coalesce(max('S'),'N')
into STRICT	ie_possui_apae_w
from	aval_pre_anestesica
where	cd_pessoa_fisica	=	cd_pessoa_fisica_w
and	coalesce(nr_cirurgia::text, '') = ''
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	dt_avaliacao > clock_timestamp() - interval '100 days';

return 	ie_possui_apae_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
