SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_apenas_procedimentos ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


Qt_registro_w		bigint;
Qt_total_w		 bigint;
Ds_result_w		 varchar(1);


BEGIN
Ds_result_w	:= 'N';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	count(nr_seq_solucao)
	into STRICT	Qt_registro_w
	from	prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S'
	and	coalesce(ie_hemodialise,'N')	= 'N';

	if (Qt_registro_w > 0) then
		 Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	select 	count(a.nr_sequencia)
	into STRICT	Qt_registro_w
	from 	material b,
			prescr_material a
	where 	a.cd_material    	= b.cd_material
	and 	a.nr_prescricao  	= nr_prescricao_p
	and		a.ie_agrupador		= 2
	and 	coalesce(a.nr_sequencia_solucao::text, '') = ''
	and 	coalesce(a.nr_sequencia_proc::text, '') = ''
	and 	coalesce(a.nr_sequencia_dieta::text, '') = ''
	and		coalesce(a.ie_suspenso,'N')		<> 'S'
	and 	coalesce(a.nr_seq_kit::text, '') = '';

	if (Qt_registro_w > 0) then
		 Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	select	count(nr_sequencia)
	into STRICT	Qt_registro_w
	from	HD_PRESCRICAO
	where	nr_prescricao	= nr_prescricao_p;

	if (Qt_registro_w <> 0) then
		select	count(*)
		into STRICT	Qt_registro_w
		from	prescr_solucao
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S'
		and	coalesce(ie_hemodialise,'N')	in ('S','P');

		if (Qt_registro_w > 0) then
			Qt_total_w	:= Qt_total_w + Qt_registro_w;
		end if;
	end if;

	select	count(nr_sequencia)
	into STRICT	Qt_registro_w
	from	rep_jejum
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S';

	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	select	count(nr_sequencia)
	into STRICT	Qt_registro_w
	from	prescr_recomendacao
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S';

	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	Select sum(ct)
	into STRICT	Qt_registro_w
	from (	SELECT	count(nr_sequencia) ct

		from	NUT_PACIENTE
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S'
		
union all

		SELECT	count(nr_sequencia)
		from	NUT_PAC
		where	nr_prescricao	= nr_prescricao_p
		and 	coalesce(ie_npt_adulta,'N')  = 'S'
		and	coalesce(ie_suspenso, 'N')	<> 'S'
	) alias6;

	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	select	count(*)
	into STRICT	Qt_registro_w
	from	NUT_PAC
	where	nr_prescricao	= nr_prescricao_p
	and 	coalesce(ie_npt_adulta,'N') <> 'S'
	and		coalesce(ie_suspenso, 'N')	<> 'S';

	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;


	select	count(nr_sequencia)
	into STRICT	Qt_registro_w
	from	prescr_solic_bco_sangue a,
			prescr_medica b
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_prescricao = b.nr_prescricao
	and	coalesce(b.dt_suspensao::text, '') = '';

	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	select	count(nr_sequencia)
	into STRICT	qt_registro_w
	from	prescr_leite_deriv
	where	nr_prescricao = nr_prescricao_p;
	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

	select	count(nr_sequencia)
	into STRICT	Qt_registro_w
	from	prescr_gasoterapia
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S';
	if (Qt_registro_w > 0) then
		Qt_total_w	:= Qt_total_w + Qt_registro_w;
	end if;

end if;

if (Qt_registro_w > 0) then
	Ds_result_w := 'S';
end if;

return Ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_apenas_procedimentos ( nr_prescricao_p bigint) FROM PUBLIC;

