CREATE OR REPLACE FUNCTION obter_se_aplica_controle_sc ( nr_seq_regra_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE



ie_aplica_w		varchar(15) := 'S';
qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	regra_contr_qtde_sc_atrib
where	nr_seq_regra = nr_seq_regra_p;

if (qt_registros_w = 0) then
	ie_aplica_w := 'S';
else
	select	count(*)
	into STRICT	qt_registros_w
	from	regra_contr_qtde_sc_atrib
	where	coalesce(cd_perfil, cd_perfil_p)				= cd_perfil_p
	and	coalesce(cd_setor_atendimento, cd_setor_atendimento_p)	= cd_setor_atendimento_p
	and	coalesce(nm_usuario_regra, nm_usuario_p)			= nm_usuario_p
	and	coalesce(ie_consistir,'S') = 'N'
	and	nr_seq_regra = nr_seq_regra_p;

	if (qt_registros_w > 0) then
		ie_aplica_w := 'N';
	else
		ie_aplica_w := 'S';
	end if;

end if;


return	ie_aplica_w;

end;
$body$
