CREATE OR REPLACE FUNCTION obter_se_aprazar_item_prescr (ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_item_p bigint, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_aprazar_w	varchar(1) := 'S';


BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then

	if (ie_tipo_item_p = 'M') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aprazar_w
		from	prescr_mat_hor
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_material = nr_seq_item_p
		and	dt_horario = dt_horario_p
		and	ie_agrupador = 1
		and	coalesce(ie_situacao,'A') = 'A'
		and	coalesce(ie_adep,'S') = 'S'
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'S') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aprazar_w
		from	prescr_mat_hor
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_material = nr_seq_item_p
		and	dt_horario = dt_horario_p
		and	ie_agrupador = 12
		and	coalesce(ie_situacao,'A') = 'A'
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	--elsif	(ie_tipo_item_p = 'P') then
	elsif (ie_tipo_item_p in ('P','G','C','I')) then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aprazar_w
		from	prescr_proc_hor
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_procedimento = nr_seq_item_p
		and	dt_horario = dt_horario_p
		and	coalesce(ie_situacao,'A') = 'A'
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'R') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aprazar_w
		from	prescr_rec_hor
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_recomendacao = nr_seq_item_p
		and	dt_horario = dt_horario_p
		and	coalesce(ie_situacao,'A') = 'A'
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'E') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aprazar_w
		from	pe_prescr_proc_hor
		where	nr_seq_pe_prescr = nr_prescricao_p
		and	nr_seq_pe_proc = nr_seq_item_p
		and	dt_horario = dt_horario_p
		and	coalesce(ie_situacao,'A') = 'A';

	elsif (ie_tipo_item_p = 'D') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aprazar_w
		from	prescr_dieta_hor
		where	nr_prescricao = nr_prescricao_p
		and	dt_horario = dt_horario_p
		and	coalesce(ie_situacao,'A') = 'A'
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
	end if;
end if;

return ie_aprazar_w;

end;
$body$
