CREATE OR REPLACE FUNCTION obter_se_apraza_etapa ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprazar_w	varchar(1) := 'N';


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_aprazar_w
	from	adep_processo
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p
	and	nr_etapa	= nr_etapa_p
	and	coalesce(dt_cancelamento::text, '') = '';
	end;
end if;
return ie_aprazar_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
