CREATE OR REPLACE FUNCTION obter_se_aprazou_hor ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';


BEGIN

if (ie_tipo_item_p in ('M', 'MAT')) then

    select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	prescr_mat_hor
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_material = nr_seq_item_p
	and		coalesce(ie_aprazado, 'N') = 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
