CREATE OR REPLACE FUNCTION obter_se_apraz_item_vig_adep ( nr_seq_mat_cpoe_p bigint, dt_inicio_prescr_p timestamp, dt_inicio_param_p timestamp, dt_final_param_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_needs_schedule_w   varchar(1) := 'N';
ie_alterou_horario_w	cpoe_material.ie_alterou_horario%type;
ie_sem_aprazamento_w	cpoe_material.ie_sem_aprazamento%type;


BEGIN
	
	if (coalesce(nr_seq_mat_cpoe_p,0) > 0) then
	
		select	ie_alterou_horario,
            ie_sem_aprazamento
		into STRICT	  ie_alterou_horario_w,
				    ie_sem_aprazamento_w
		from	  cpoe_material
		where	  nr_sequencia = nr_seq_mat_cpoe_p;	

	
	  if (ie_alterou_horario_w = 'S' and ie_sem_aprazamento_w = 'S') then
		  if (trunc(dt_inicio_prescr_p) between trunc(dt_inicio_param_p) and trunc(dt_final_param_p)) then
			  ie_needs_schedule_w := 'S';
			else
			  ie_needs_schedule_w := 'N';
			end if;				
		else
			ie_needs_schedule_w := 'N';
		end if;
		
	else
		ie_needs_schedule_w := 'N';
	end if;	


  return ie_needs_schedule_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
