CREATE OR REPLACE FUNCTION obter_se_apraz_proc_adep ( cd_procedimento_p bigint, nr_seq_proc_interno_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_aprazar_w	varchar(1);
ie_existe_regra_w	varchar(1);
ie_permite_w		varchar(1);
cd_tipo_proc_w		bigint;
nr_sequencia_w		bigint;
cont_w			bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_permite,'S'),
	nr_sequencia
from	adep_regra_aprazamento
where	((coalesce(cd_procedimento::text, '') = '') or (cd_procedimento = cd_procedimento_p))
and	((coalesce(nr_seq_proc_interno::text, '') = '') or (nr_seq_proc_interno = nr_seq_proc_interno_p))
and	((coalesce(cd_tipo_procedimento::text, '') = '') or (cd_tipo_procedimento = cd_tipo_proc_w))
order by coalesce(cd_procedimento,0),
	coalesce(nr_seq_proc_interno,0),
	coalesce(cd_tipo_procedimento,0);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_existe_regra_w
from	adep_regra_aprazamento;

if (ie_existe_regra_w = 'S') then

	if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then

		select	max(cd_tipo_procedimento)
		into STRICT	cd_tipo_proc_w
		from	procedimento
		where	cd_procedimento = cd_procedimento_p;

		open C01;
		loop
		fetch C01 into
			ie_permite_w,
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			select	count(*)
			into STRICT	cont_w
			from	adep_aprazamento_perfil
			where	nr_seq_regra	= nr_sequencia_w;

			if (cont_w	> 0) then
				select	count(*)
				into STRICT	cont_w
				from	adep_aprazamento_perfil
				where	nr_seq_regra	= nr_sequencia_w
				and	cd_perfil	= cd_perfil_p;

				if (cont_w	 > 0) then
					ie_permite_aprazar_w	:= ie_permite_w;
				else
					ie_permite_aprazar_w	:= 'S';
				end if;
			else
				ie_permite_aprazar_w	:= ie_permite_w;
			end if;
		end loop;
		close C01;

	else
		ie_permite_aprazar_w := 'S';
	end if;
else
	ie_permite_aprazar_w := 'S';
end if;

return	coalesce(ie_permite_aprazar_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
