CREATE OR REPLACE FUNCTION obter_se_apresenta_carta ( dt_liberacao_p timestamp, nm_usuario_original_p text, cd_medico_responsavel_p text, nm_usuario_p text, cd_pessoa_usuario_p text, ie_medico_p text, nr_seq_carta_mae_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ie_apresenta_w		varchar(1) := 'N';


BEGIN 
 
if (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') or (nm_usuario_p = nm_usuario_original_p) or (cd_medico_responsavel_p = cd_pessoa_usuario_p) or 
	((ie_medico_p = 'S') and (coalesce(cd_medico_responsavel_p::text, '') = '')) then 
	ie_apresenta_w	:= 'S';
end if;
 
if (ie_apresenta_w	= 'N') then 
 
	select	CASE WHEN count(nr_sequencia)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_apresenta_w 
	from	carta_medica 
	where	coalesce(nr_seq_carta_mae,nr_sequencia) = nr_seq_carta_mae_p 
	and		cd_medico_responsavel = cd_pessoa_usuario_p;
	 
end if;
 
return	ie_apresenta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
