CREATE OR REPLACE FUNCTION obter_se_apresenta_coluna (nr_seq_objeto_p bigint, nm_campo_base_p text, cd_perfil_p bigint, nm_usuario_p text, ie_configuravel_p text) RETURNS varchar AS $body$
DECLARE


ie_exibir_atributo_w	varchar(1);

C01 CURSOR FOR
	SELECT	a.ie_exibir_atributo
	from	wcp_regra_atributo a,
		dic_objeto b
	where	a.nr_seq_atributo  	 		= b.nr_sequencia
	and	a.nr_seq_wcpanel  			= nr_seq_objeto_p
	and	b.nm_campo_base   			= nm_campo_base_p
	and	coalesce(nm_usuario_regra,nm_usuario_p) 	= nm_usuario_p
	and	coalesce(cd_perfil,cd_perfil_p) 		= cd_perfil_p
	order by
	coalesce(nm_usuario_regra,'ZZZZZZZZZZZZZ'),
	coalesce(cd_perfil,0) LIMIT 1;


BEGIN

if (ie_configuravel_p = 'N') then
	return 'S';
end if;

open C01;
loop
fetch C01 into
	ie_exibir_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if ((ie_configuravel_p = 'S') or (ie_configuravel_p = 'R')) then
	return	coalesce(ie_exibir_atributo_w, 'N');
end if;

return	coalesce(ie_exibir_atributo_w, 'S');

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
