CREATE OR REPLACE FUNCTION obter_se_apresenta_dominio ( cd_dominio_p bigint, vl_dominio_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_registro_w	smallint := 0;
ds_locale_w		valor_dominio_pais.ds_locale%type;


BEGIN

ds_locale_w := coalesce(coalesce(pkg_i18n.get_user_locale,philips_param_pck.get_ds_locale),'pt_BR');


select	count(1)
into STRICT	qt_registro_w
from	valor_dominio_pais
where	cd_dominio = cd_dominio_p
and	vl_dominio = vl_dominio_p
and		ds_locale = ds_locale_w;

if (qt_registro_w > 0) then
	ds_retorno_w := 'S';
else
	select	count(1)
	into STRICT	qt_registro_w
	from	valor_dominio_pais
	where	cd_dominio = cd_dominio_p
	and	vl_dominio = vl_dominio_p;

	if (qt_registro_w = 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end	if;
end	if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
