CREATE OR REPLACE FUNCTION obter_se_apresenta_parametro ( cd_funcao_p bigint, nr_seq_param_p bigint ) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(1);
cd_pais_w	valor_dominio_pais.cd_pais%type;
qt_registro_w	smallint := 0;


BEGIN


cd_pais_w := coalesce(philips_param_pck.get_cd_pais, 0);
if (cd_pais_w = 0) then
	cd_pais_w := 1;
end if;

select  count(1)
into STRICT	qt_registro_w
from    funcao_param_pais
where   cd_funcao = cd_funcao_p
and	nr_seq_param = nr_seq_param_p;

if (qt_registro_w = 0) then
	ds_retorno_w := 'S';
else
	select  count(1)
	into STRICT	qt_registro_w
	from    funcao_param_pais
	where   cd_funcao = cd_funcao_p
	and	nr_seq_param = nr_seq_param_p
	and	cd_pais = cd_pais_w;

	if (qt_registro_w = 0) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
