CREATE OR REPLACE FUNCTION obter_se_apres_turno_apap ( ie_turno_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE



dt_inicial_w			timestamp;
dt_final_w			timestamp;
dt_atual_w			timestamp;


C01 CURSOR FOR
	SELECT	to_date('01/01/1999' || ' ' || to_char(dt_inicial,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') dt_inicial,
		to_date('01/01/1999' || ' ' || to_char(dt_final,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') dt_final,
		to_date('01/01/1999' || ' ' || to_char(clock_timestamp(),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') dt_atual
	from	regra_turno_gp
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_turno_apap		= ie_turno_p
	and (cd_setor_atendimento = coalesce(cd_setor_atendimento_p, cd_setor_atendimento) or coalesce(cd_setor_atendimento::text, '') = '');



BEGIN

open C01;
loop
fetch C01 into
	dt_inicial_w,
	dt_final_w,
	dt_atual_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if	(dt_atual_w >= dt_inicial_w AND dt_atual_w <= dt_final_w) or
		((dt_final_w < dt_inicial_w) and
		 ((dt_atual_w >= dt_inicial_w) or (dt_atual_w <= dt_final_w))) then
		return 'S';
	end if;
	end;
end loop;
close C01;


return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
