CREATE OR REPLACE FUNCTION obter_se_aprovador ( cd_tipo_pessoa_p bigint, cd_perfil_p bigint, cd_pessoa_p text ) RETURNS varchar AS $body$
DECLARE


  ie_aprovador_w  varchar(1);


BEGIN

  select CASE WHEN sum(qtd)=0 THEN 'N'  ELSE 'S' END
    into STRICT ie_aprovador_w
    from ( SELECT count(1) qtd
             from regra_pre_cad_pj a, regra_pre_cad_pj_aprov b
            where a.nr_sequencia = b.nr_seq_regra
              and coalesce(a.cd_tipo_pessoa::text, '') = ''
              and b.cd_perfil = cd_perfil_p
              and coalesce(b.cd_pessoa_aprov::text, '') = ''
            
union

           SELECT count(1) qtd
             from regra_pre_cad_pj a, regra_pre_cad_pj_aprov b
            where a.nr_sequencia = b.nr_seq_regra
              and coalesce(a.cd_tipo_pessoa::text, '') = ''
              and coalesce(b.cd_perfil::text, '') = ''
              and b.cd_pessoa_aprov = cd_pessoa_p
            
union

           select count(1) qtd
             from regra_pre_cad_pj a, regra_pre_cad_pj_aprov b
            where a.nr_sequencia = b.nr_seq_regra
              and a.cd_tipo_pessoa = cd_tipo_pessoa_p
              and b.cd_perfil = cd_perfil_p
              and coalesce(b.cd_pessoa_aprov::text, '') = ''
            
union

           select count(1) qtd
             from regra_pre_cad_pj a, regra_pre_cad_pj_aprov b
            where a.nr_sequencia = b.nr_seq_regra
              and a.cd_tipo_pessoa = cd_tipo_pessoa_p
              and coalesce(b.cd_perfil::text, '') = ''
              and b.cd_pessoa_aprov = cd_pessoa_p ) alias11;

  return ie_aprovador_w;

end;
$body$
