CREATE OR REPLACE FUNCTION obter_se_aprov_audit_justif ( nr_seq_auditoria_p bigint, nr_seq_item_audit_p bigint, cd_cargo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w		bigint;
nr_nivel_w		smallint;
nr_nivel_ant_w		smallint;
nm_usuario_aprovador_w	varchar(15);
cd_cargo_aprovador_w	bigint;
ie_exige_justif_w	varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	nr_seq_regra_aprovacao,
		coalesce(nr_nivel,0),
		nm_usuario_aprovador,
		cd_cargo_aprovador
	from	auditoria_aprovacao_item
	where	nr_seq_auditoria = nr_seq_auditoria_p
	and	nr_seq_item = nr_seq_item_audit_p
	and	coalesce(dt_aprovacao::text, '') = ''
	order by	coalesce(nr_nivel,0);


BEGIN

if (coalesce(nr_seq_auditoria_p,0) > 0) and (coalesce(nr_seq_item_audit_p,0) > 0) then

	nr_nivel_ant_w	:= 0;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_w,
		nr_nivel_w,
		nm_usuario_aprovador_w,
		cd_cargo_aprovador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (nr_nivel_ant_w = 0) or (nr_nivel_w = nr_nivel_ant_w) then

			nr_nivel_ant_w	:= nr_nivel_w;

			if	(nm_usuario_aprovador_w IS NOT NULL AND nm_usuario_aprovador_w::text <> '' AND nm_usuario_aprovador_w = nm_usuario_p) or
				(cd_cargo_aprovador_w IS NOT NULL AND cd_cargo_aprovador_w::text <> '' AND cd_cargo_aprovador_w = cd_cargo_p) or (audit_obter_pessoa_substituta(cd_cargo_aprovador_w, nm_usuario_aprovador_w, nm_usuario_p) = 'S')  then

				select	max(ie_exige_justificativa)
				into STRICT	ie_exige_justif_w
				from	regra_aprovacao_auditoria
				where	nr_sequencia = nr_seq_regra_w;

			end if;

		end if;

		end;
	end loop;
	close C01;

end if;

return	ie_exige_justif_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
