CREATE OR REPLACE FUNCTION obter_se_aprov_encaminhado ( nr_sequencia_p bigint, nr_seq_proc_aprov_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_proc_original_w				processo_aprov_compra.nr_seq_proc_original%type;
ie_aprov_reprov_w				processo_aprov_compra.ie_aprov_reprov%type;
ie_processo_encaminhado_w			varchar(1);


BEGIN
ie_processo_encaminhado_w := 'N';

/*Busca o processo de aprovação original, ou seja, do processo da pessoa que encaminhou*/

select	coalesce(max(nr_seq_proc_original),0)
into STRICT	nr_seq_proc_original_w
from	processo_aprov_compra
where	nr_sequencia = nr_sequencia_p
and	nr_seq_proc_aprov = nr_seq_proc_aprov_p;

if (nr_seq_proc_original_w > 0) then

	/*Busca o ie_aprov_reprov do processo que fez o encaminhamento. O processo da pessoa que encaminhou, sempre fica como E*/

	select	ie_aprov_reprov
	into STRICT	ie_aprov_reprov_w
	from	processo_aprov_compra
	where	nr_sequencia = nr_sequencia_p
	and	nr_seq_proc_aprov = nr_seq_proc_original_w;

	if (ie_aprov_reprov_w = 'E') then
		ie_processo_encaminhado_w := 'S';
	end if;
end if;

return	ie_processo_encaminhado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_aprov_encaminhado ( nr_sequencia_p bigint, nr_seq_proc_aprov_p bigint) FROM PUBLIC;

