SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_aprov_laudo ( nr_seq_laudo_p bigint, nm_usuario_p text ) RETURNS varchar AS $body$
DECLARE

 
cd_medico_w		varchar(10);
qt_registro_w		bigint;
cd_pessoa_fisica_w	varchar(10);
ie_retorno_w		varchar(1) := 'S';


BEGIN 
 
select	coalesce(max(cd_medico_resp),0) 
into STRICT	cd_medico_w 
from	laudo_paciente 
where	nr_sequencia	=	nr_seq_laudo_p;
 
select	count(*) 
into STRICT	qt_registro_w 
from 	regra_aprov_laudo 
where	cd_medico	=	cd_medico_w;
 
if (qt_registro_w > 0) then 
	select	coalesce(Obter_Dados_Usuario_Opcao(nm_usuario_p,'C'),'0') 
	into STRICT	cd_pessoa_fisica_w 
	;
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_retorno_w 
	from	regra_aprov_laudo 
	where	cd_medico		=	cd_medico_w 
	and	cd_pessoa_fisica	=	cd_pessoa_fisica_w;
 
	if (cd_medico_w = cd_pessoa_fisica_w) then 
		ie_retorno_w := 'S';
	end if;
else 
	ie_retorno_w := 'S';
end if;
	 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
