CREATE OR REPLACE FUNCTION obter_se_ap_lote_bloqueio ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



nr_sequencia_w		bigint;
nm_usuario_w		varchar(15);
dt_atualizacao_w		timestamp;
dt_tentativa_w		timestamp;
ie_bloqueado_w		varchar(1);
qt_min_controle_lote_w	bigint;
qt_min_diferenca_w		double precision;
ds_retorno_w		varchar(255);
ie_explicit_hold_w	varchar(1);


BEGIN


ie_bloqueado_w	:= 'N';
ie_explicit_hold_w := 'N';

select	coalesce(max(qt_min_controle_lote), 0)
into STRICT	qt_min_controle_lote_w
from	parametros_farmacia
where	cd_estabelecimento		= cd_estabelecimento_p;

select	coalesce(max(nr_sequencia), 0)
into STRICT	nr_sequencia_w
from	ap_lote_bloqueio
where	nr_seq_lote	= nr_seq_lote_p;

if (nr_sequencia_w > 0) then
	begin
	select	nm_usuario,
			dt_atualizacao,
			coalesce(ie_explicit_hold,'N')
	into STRICT	nm_usuario_w,
			dt_atualizacao_w,
			ie_explicit_hold_w
	from	ap_lote_bloqueio
	where	nr_sequencia	= nr_sequencia_w;
			
	end;
end if;

if (ie_explicit_hold_w = 'S') then

/*	if	(nm_usuario_w = nm_usuario_p) then
		ie_bloqueado_w	:= 'N';
	else
		ie_bloqueado_w	:= 'S';
	end if; */
  ie_bloqueado_w	:= 'S';
	
elsif (qt_min_controle_lote_w > 0) then
	begin

	if (nr_sequencia_w > 0) then
		begin
		
		if (nm_usuario_w = nm_usuario_p) then
			ie_bloqueado_w	:= 'N';

		elsif (nm_usuario_w <> nm_usuario_p) then
			begin

			dt_tentativa_w		:= clock_timestamp();
			qt_min_diferenca_w	:= ((dt_tentativa_w - dt_atualizacao_w) * 1440);


			if (qt_min_diferenca_w < qt_min_controle_lote_w) then
				ie_bloqueado_w	:= 'S';
			else
				ie_bloqueado_w	:= 'N';
			end if;

			end;
		end if;
		end;
	end if;
	end;
end if;

return ie_bloqueado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
