CREATE OR REPLACE FUNCTION obter_se_area_lote_fornec ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1) := 'N';

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	coalesce(max(ie_adm_lote_fornec),'N')
	into STRICT		ie_retorno_w
	from		adep_area_prep
	where	nr_sequencia = nr_sequencia_p;
end if;

return ie_retorno_w;

end;
$body$
