SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_arquetipo_vinc ( CD_NODE_P text, CD_ARQUETIPO_p text, NR_SEQUENCIA_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1) := 'N';
ds_elemento_w	ehr_arq_elemento.ds_elemento_formal%type;
cd_arquetipo_w	ehr_arquetipo.cd_arquetipo%type;



BEGIN

if (CD_NODE_P IS NOT NULL AND CD_NODE_P::text <> '' AND CD_ARQUETIPO_p IS NOT NULL AND CD_ARQUETIPO_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from   	ehr_elemento_conceito
	where 	trim(both upper(cd_node)) = trim(both upper(cd_node_p))
	and 	trim(both upper(cd_arquetipo)) = trim(both upper(cd_arquetipo_p));

	if (ds_retorno_w = 'N') then

		select replace(replace(regexp_substr(ds_elemento_formal, '{\/(.*)\/}'),'{/',''),'/}','')
		into STRICT ds_elemento_w
		from ehr_arq_elemento
		where cd_classe = 'CLUSTER'
		and	nr_sequencia = nr_sequencia_p;

		if (length(trim(both ds_elemento_w)) > 0) then

			select 	regexp_substr(cd_arquetipo, ds_elemento_w)
			into STRICT	cd_arquetipo_w
			from	ehr_arquetipo LIMIT 1;

			if (length(trim(both cd_arquetipo_w)) > 0) then
				ds_retorno_w := 'A';
			end if;
		end if;
	end if;



end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
