CREATE OR REPLACE FUNCTION obter_se_assinado_digital ( NR_PRESCRICAO_P bigint, NR_SEQ_PRESCR_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_resultado_w	exame_lab_resultado.nr_seq_resultado%type;
nr_seq_assinatura_w	exame_lab_result_item.nr_seq_assinatura%type;

BEGIN
	ds_retorno_w:= 'N';

	select	MAX(nr_seq_resultado)
	into STRICT	nr_seq_resultado_w
	from	exame_lab_resultado
	where	nr_prescricao = nr_prescricao_p;

	if (nr_seq_resultado_w IS NOT NULL AND nr_seq_resultado_w::text <> '') then
		select	max(nr_Seq_Assinatura)
		into STRICT	nr_seq_assinatura_w
		from	exame_lab_result_item
		where	nr_seq_resultado = nr_seq_resultado_w
		and		nr_seq_prescr = nr_seq_prescr_p
		and 	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');
	end if;

	if (coalesce(nr_seq_assinatura_w::text, '') = '') then
		ds_retorno_w:= 'N';
	else
		ds_retorno_w:= 'S';
	end if;

	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
