CREATE OR REPLACE FUNCTION obter_se_associado ( nr_prescricao_p bigint, nr_seq_superior_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w			varchar(1);
nr_seq_superior_w	prescr_mat_hor.nr_seq_superior%type;


BEGIN

if (nr_seq_superior_p = nr_seq_item_p) then
	return 'S';
end if;

select	coalesce(max(nr_seq_superior),0)
into STRICT	nr_seq_superior_w
from 	prescr_mat_hor
where 	nr_prescricao = nr_prescricao_p
and 	nr_seq_material = nr_seq_superior_p;

if (nr_seq_superior_w = nr_seq_item_p) then
	return 'S';
elsif (nr_seq_superior_w > 0) then
	return obter_se_associado(nr_prescricao_p, nr_seq_superior_w, nr_seq_item_p);
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
