CREATE OR REPLACE FUNCTION obter_se_assoc_ccg_adm (nr_prescricao_p bigint, nr_sequencia_proc_p bigint, dt_horario_p timestamp ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := '';

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_proc_p IS NOT NULL AND nr_sequencia_proc_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then
	begin

	select	max('A')
	into STRICT 	ds_retorno_w
	from	prescr_mat_hor a,
		prescr_material b
	where	b.nr_prescricao = a.nr_prescricao
	and	b.nr_sequencia = a.nr_seq_material
	and	b.nr_prescricao = nr_prescricao_p
	and	b.nr_sequencia_proc = nr_sequencia_proc_p
	and	a.dt_horario = dt_horario_p
	and	(a.dt_fim_horario IS NOT NULL AND a.dt_fim_horario::text <> '')
	and	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S';

	if (coalesce(ds_retorno_w::text, '') = '') then
		begin

		select	max('S')
		into STRICT 	ds_retorno_w
		from	prescr_mat_hor a,
			prescr_material b
		where	b.nr_prescricao = a.nr_prescricao
		and	b.nr_sequencia = a.nr_seq_material
		and	b.nr_prescricao = nr_prescricao_p
		and	b.nr_sequencia_proc = nr_sequencia_proc_p
		and	a.dt_horario = dt_horario_p
		and	(a.dt_suspensao IS NOT NULL AND a.dt_suspensao::text <> '')
		and	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S';

		end;
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
