CREATE OR REPLACE FUNCTION obter_se_assoc_glic_pend ( nr_prescricao_p bigint, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_assoc_glic_pend_w	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_assoc_glic_pend_w
from	prescr_mat_hor
where	nr_prescricao = nr_prescricao_p
and	dt_horario = dt_horario_p
and	ie_agrupador = 5
and	coalesce(dt_fim_horario::text, '') = ''
and	coalesce(dt_suspensao::text, '') = ''
and	coalesce(ie_horario_especial,'N') <> 'S';

return	ie_assoc_glic_pend_w;

end;
