CREATE OR REPLACE FUNCTION obter_se_assumir_paciente ( cd_medico_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atende_w		varchar(1);
ie_regra_medico_pac_w	varchar(2);


BEGIN

select	coalesce(ie_regra_medico_pac,'T')
into STRICT	ie_regra_medico_pac_w
from	estabelecimento
where	cd_estabelecimento	=	cd_estabelecimento_p;

select	CASE WHEN obter_se_corpo_clinico(cd_medico_p)='S' THEN 'S'  ELSE 'N' END
into STRICT	ie_atende_w
;

if (ie_regra_medico_pac_w = 'C') then
	return ie_atende_w;
elsif (ie_regra_medico_pac_w = 'R') then
	if (ie_atende_w = 'S') then
		begin
		select	ie_atende
		into STRICT	ie_atende_w
		from	regra_medico_estab
		where	cd_medico		=	cd_medico_p
		and	cd_estabelecimento	=	cd_estabelecimento_p
		and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp() - interval '1 days') and coalesce(dt_final_vigencia,clock_timestamp() + interval '1 days');
		exception
		when others then
			ie_atende_w	:= 'S';
		end;
	else
		begin
		select	ie_atende
		into STRICT	ie_atende_w
		from	regra_medico_estab
		where	cd_medico		=	cd_medico_p
		and	cd_estabelecimento	=	cd_estabelecimento_p
		and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp() - interval '1 days') and coalesce(dt_final_vigencia,clock_timestamp() + interval '1 days');
		exception
		when others then
			ie_atende_w	:= 'N';
		end;
	end if;
else
	ie_atende_w	:=	'S';
end if;

return	ie_atende_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
