CREATE OR REPLACE FUNCTION obter_se_assumir_pa_regra ( nr_atendimento_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(1) := 'N';
qt_medico_regra_w	integer;
cd_medico_resp_w	varchar(10);
qt_registros_w	integer;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	select	count(*) 
	into STRICT	qt_medico_regra_w 
	from	regra_assumir_pac_pa;
 
	if (qt_medico_regra_w > 0) then 
		begin 
		select	max(a.cd_medico_resp) 
		into STRICT	cd_medico_resp_w 
		from	atendimento_paciente a 
		where	a.nr_atendimento = nr_atendimento_p;
 
		if (cd_medico_resp_w IS NOT NULL AND cd_medico_resp_w::text <> '') then 
			begin 
			if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_medico_p = cd_medico_resp_w) then 
				ds_retorno_w := 'S';
			else 
				begin 
				select	count(*) 
				into STRICT	qt_registros_w 
				from	regra_assumir_pac_pa a 
				where	cd_pessoa_fisica = cd_medico_resp_w;
 
				if (qt_registros_w > 0) then 
					ds_retorno_w := 'S';
				end if;
				end;
			end if;
			end;
		end if;
		end;
	else 
		ds_retorno_w := 'S';
	end if;
	end;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
