SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atc_mat ( cd_material_p bigint, cd_substancia_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w		bigint	:= 0;
ie_retorno_w		varchar(1)	:= 'N';


BEGIN

begin
select	count(*)
into STRICT	qt_registros_w
from	material_atc
where	cd_material	= cd_material_p
and	cd_substancia	= cd_substancia_p;
exception
	when others then
	qt_registros_w	:= 0;
end;

if (qt_registros_w	> 0) then
	ie_retorno_w	:= 'S';
else
	begin
	select	count(*)
	into STRICT	qt_registros_w
	from	material a
	where	a.cd_material	= cd_material_p
	and	a.nr_seq_ficha_tecnica in (
		SELECT	nr_seq_ficha_tecnica b
		from	medic_ficha_tecnica_atc b
		where	b.cd_substancia_atc	= cd_substancia_p );
	exception
		when others then
		qt_registros_w	:= 0;
	end;

	if (qt_registros_w > 0) then
		ie_retorno_w	:= 'S';
	end if;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
