CREATE OR REPLACE FUNCTION obter_se_atendimento_futuro ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';

dt_atualizacao_nrec_w		atendimento_paciente.dt_atualizacao_nrec%type;
dt_entrada_w				atendimento_paciente.dt_entrada%type;
dt_chegada_paciente_w		atendimento_paciente.dt_chegada_paciente%type;				


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	Select  max(dt_atualizacao_nrec),
			max(dt_entrada),
			max(dt_chegada_paciente)
	into STRICT	dt_atualizacao_nrec_w,
			dt_entrada_w,
			dt_chegada_paciente_w
	from    atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;
	
	
	if (dt_atualizacao_nrec_w < dt_entrada_w and coalesce(dt_chegada_paciente_w::text, '') = '') then
	
		ie_retorno_w := 'S';
	
	end if;
	
	
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
