CREATE OR REPLACE FUNCTION obter_se_atendimento_tem_setor ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_passou_setor_w	varchar(1);


BEGIN

select 	CASE WHEN count(*)=0 THEN  'S'  ELSE 'N' END
into STRICT 	ie_passou_setor_w
from 	atendimento_paciente
where  	nr_atendimento = nr_atendimento_p
and 	coalesce(dt_cancelamento::text, '') = '';

if (coalesce(ie_passou_setor_w, 'N') = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_passou_setor_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p;
end if;

return ie_passou_setor_w;

end;
