CREATE OR REPLACE FUNCTION obter_se_atend_agenda_aberto (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1);
nr_atendimento_w	bigint;
ie_conta_aberto_w	varchar(1);
ie_fim_conta_w		varchar(1);


BEGIN 
 
ds_retorno_w:= 'S';
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
 
	select	coalesce(max(nr_atendimento),0) 
	into STRICT	nr_atendimento_w 
	from	agenda_consulta 
	where	nr_sequencia = nr_sequencia_p;
 
	if (nr_atendimento_w > 0) then 
		select	obter_se_atend_conta_aberto(nr_atendimento), 
			ie_fim_conta		 
		into STRICT	ie_conta_aberto_w, 
			ie_fim_conta_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_w;
 
		if (ie_fim_conta_w = 'F') or (ie_conta_aberto_w = 'N') then 
			ds_retorno_w := 'N';
		end if;
	end if;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
