CREATE OR REPLACE FUNCTION obter_se_atend_agenda_cirur ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



qt_agenda_w	smallint := 0;


BEGIN

select	count(*)
into STRICT	qt_agenda_w
from	agenda_paciente
where	nr_atendimento	=	nr_atendimento_p
and	ie_status_agenda not in ('C','E')
and 	dt_agenda between(clock_timestamp() - interval '2 days') and (clock_timestamp() + interval '1 days');

if (qt_agenda_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
