CREATE OR REPLACE FUNCTION obter_se_atend_agenda_pac (nr_atendimento_p bigint, cd_agenda_p text) RETURNS varchar AS $body$
DECLARE


ie_agenda_w	varchar(1)	:= 'S';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then


	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_agenda_w
	from	agenda_consulta a
	where	a.nr_atendimento = nr_atendimento_p
	and	a.cd_agenda = cd_agenda_p;

	if (ie_agenda_w = 'N') then
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_agenda_w
		from	agenda_paciente a
		where	a.nr_atendimento = nr_atendimento_p
		and	a.cd_agenda = cd_agenda_p;
	end if;
end if;

Return	ie_agenda_w;

END	;
