SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atend_agenda (nr_atendimento_p bigint, ie_agenda_p text) RETURNS varchar AS $body$
DECLARE


ie_agenda_w	varchar(1)	:= 'N';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	if (coalesce(ie_agenda_p, 'C') = 'C') then
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_agenda_w
		from	agenda_consulta
		where	nr_atendimento = nr_atendimento_p;

	elsif (coalesce(ie_agenda_p, 'C') = 'E') then
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_agenda_w
		from	agenda_paciente
		where	nr_atendimento = nr_atendimento_p;
	end if;
end if;

Return	ie_agenda_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
