CREATE OR REPLACE FUNCTION obter_se_atend_alerta_sepse ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


qt_alerta_w	integer;
vl_retorno_w	varchar(1) := 'N';
			

BEGIN

select 	count(*)
into STRICT	qt_alerta_w
from 	sepsis_alert
where 	nr_atendimento = nr_atendimento_p
and	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (qt_alerta_w > 0) then
	vl_retorno_w := 'S';
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
