CREATE OR REPLACE FUNCTION obter_se_atend_aprovado ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprov_w		varchar(1);
qt_exame_pendente_w	bigint;
qt_exame_aprov_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_exame_pendente_w
from	prescr_medica b,
	prescr_procedimento a
where	a.nr_prescricao = b.nr_prescricao
and	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
and	a.ie_status_atend < 35
and	a.cd_motivo_baixa = 0
and	b.nr_atendimento = nr_atendimento_p;

ie_aprov_w	:= 'N';

if (qt_exame_pendente_w = 0) then

	select 	count(*)
	into STRICT	qt_exame_aprov_w
	from	prescr_medica b,
		prescr_procedimento a
	where	a.nr_prescricao = b.nr_prescricao
	and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and	ie_status_atend >= 35
	and	nr_atendimento = nr_atendimento_p;

	if (qt_exame_aprov_w > 0)	then
		ie_aprov_w	:= 'S';
	end if;

end if;

return ie_aprov_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_atend_aprovado ( nr_atendimento_p bigint) FROM PUBLIC;

