CREATE OR REPLACE FUNCTION obter_se_atend_bloqueado (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1):= 'N';
ie_bloqueio_w	varchar(1);

C01 CURSOR FOR
	SELECT	ie_bloqueio
	from	bloqueio_titulo_paciente
	where	nr_atendimento = nr_atendimento_p
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	ie_bloqueio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_bloqueio_w = 'B') then
		ie_retorno_w:= 'S';
	elsif (ie_bloqueio_w = 'D') then
		ie_retorno_w:= 'N';
	end if;

	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
