SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atend_ccg (nr_atendimento_p bigint, qt_hora_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ccg_w	varchar(1);


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_ccg_w
	from	prescr_procedimento b,
		prescr_medica a
	where	b.nr_prescricao		= a.nr_prescricao
	and	b.ie_suspenso			<> 'S'
	and	(b.nr_seq_proc_interno IS NOT NULL AND b.nr_seq_proc_interno::text <> '')
	and	substr(obter_ctrl_glic_proc(b.nr_seq_proc_interno),1,3) = 'CCG'
	and	coalesce(a.dt_suspensao::text, '') = ''
	--and	a.dt_inicio_prescr      	<= sysdate
	and     a.dt_validade_prescr	>= clock_timestamp() - qt_hora_prescr_p / 24
	and	a.nr_atendimento		= nr_atendimento_p;
end if;

return ie_ccg_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
