CREATE OR REPLACE FUNCTION obter_se_atend_cid ( cd_doenca_cid_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';


BEGIN

if (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') and (coalesce(nr_atendimento_p,0) > 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	diagnostico_doenca
	where	nr_atendimento		= nr_atendimento_p
	and		cd_doenca			= cd_doenca_cid_p
	and		coalesce(ie_situacao,'A') = 'A';

end if;


return	ie_retorno_w;

end;
