CREATE OR REPLACE FUNCTION obter_se_atend_cig ( nr_atendimento_p bigint, qt_hora_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cig_w	varchar(1);


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_cig_w
	from    atend_glicemia a,
	        prescr_medica b
	where   a.nr_prescricao        	= b.nr_prescricao
	and     a.nr_atendimento       	= b.nr_atendimento
	and     ((coalesce(a.nr_seq_prot_glic::text, '') = '') or (exists (SELECT 1 from pep_protocolo_glicemia c where c.nr_sequencia = a.nr_seq_prot_glic and c.ie_tipo = 'I')))
	and     b.nr_atendimento       	= nr_atendimento_p
	and     ((b.dt_validade_prescr	>= clock_timestamp() - qt_hora_prescr_p / 24) or (a.ie_status_glic <> 'P'))
	--and     a.ie_status_glic       	not in ('T','S');  		Os terminados devem aparecer...Marcus OS154531
	and	a.ie_status_glic <> 'S';

end if;

return ie_cig_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
