CREATE OR REPLACE FUNCTION obter_se_atend_cirurgia (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cirurgia_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_cirurgia_w
from	cirurgia
where	nr_atendimento		= nr_atendimento_p;

if (coalesce(ie_cirurgia_w,'N') = 'N') then
    select	coalesce(max('S'),'N')
	into STRICT  ie_cirurgia_w
	from  prescr_medica
	where nr_atendimento	= nr_atendimento_p
	and	(nr_cirurgia IS NOT NULL AND nr_cirurgia::text <> '');
end if;

return	ie_cirurgia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
